import React, { Component } from 'react'
import './setup.less';

function SetupItem(item: any) {
  console.log("data", item);
  const { length } = item;
  const style = {
    width: `${100 / (length - 1)}%`
  };
  return (
  <div className="setup-item" style={style}>
    {/* 对应每个点的圆圈，这里根据状态添加类名，样式在class样式中定义 */}
    <div className="cricle"></div>
    {/* 进度条的线条，具体样自己调整 */}
    <div className="setup-line"></div>
  </div>
  )
}

export default class Setup extends Component<any, any> {
  constructor(props: any) {
    super(props);
    this.state = {
      title: '测试进度',
      process: [1, 2, 3]
    }
  }
  render() {
    const { process } = this.state;
    return (
      <div className='setup-wrap'>
        <div className="arrow arrow-left" />
        <div className="setup-bar">
          { process && process.length ? process.map((item: any, idx: number) => {
            return <SetupItem length={process.length} idx={idx} key={item} data={item} />
          }) : null}
        </div>
        <div className="arrow arrow-right" />
      </div>
    )
  }
}
